using System;
namespace Hello
{
    class HelloWorld
    {
        static void Main()
        {
            Console.WriteLine("Hello, world!");
        }
    }
}

package main
import "fmt"
func main() {
    fmt.Println("Hello, world!")
}

public class Hello {
    public static void main(String[] args) {
        System.out.println("Hello, world!");
    }
}

SELECT *
FROM messages
WHERE text = 'Hello, world!';
